INSERT INTO psycho_question (question_text, test_type_id) VALUES
                                                              ('1. 지난주 동안 공황 발작 또는 제한된 증상 발작을 얼마나 자주 경험하셨습니까?', 7),
                                                              ('2. 만약 지난주에 공황 발작이 있었다면 공황 발작이 일어날 때 얼마나 불쾌했습니까? (* 만약 한 번 이상의 공황 발작을 경험했다면 평균적으로 평가해 주십시오.) 만약 완전한 공황 발작은 없었지만 제한된 증상 발작들만 경험했다면 제한된 증상 발작에 대해 평가하십시오.', 7),
                                                              ('3. 지난주 동안 다음 공황 발작이 일어날까 봐 또는 발작과 관련된 공포에 대해서 얼마나 불안하고 걱정하셨습니까? (예를 들어, 신체적 또는 정신적 건강 문제, 혹은 사회적으로 당황스러운 상황을 야기할까 봐 걱정하고 불안한 것을 말합니다.)', 7),
                                                              ('4. 지난주 동안 공황 발작이 올 것 같은 공포 때문에 어떤 장소나 상황(예를 들어, 대중교통, 극장, 군중들 속, 터널, 다리, 쇼핑몰, 혼자 있을 때)을 회피했거나 두려움(피하고 싶은 느낌이 드는 불편감)을 느낀 적이 있습니까? 아니면 같은 이유로 그런 상황에 처한다면 피해야만 하거나 두려움을 느낄 다른 상황이 있었습니까? 만약 어느 한 경우라도 그렇다면 지난 주 동안의 공포와 회피의 정도를 평가해 주십시오.', 7),
                                                              ('5. 지난주 동안 공황 발작 시 경험하는 것과 유사한 신체 감각을 일으키거나 혹은 공황발작을 야기할까 봐 두려워서 어떤 활동(예를 들어, 신체적 운동, 성관계, 뜨거운 물로 샤워나 목욕하기, 커피 마시기, 흥분되거나 무서운 영화 보기)을 회피했거나 두려움(피하거나 그만하고 싶을 정도로 그 활동을 하는 것이 불편함)을 느낀 적이 있습니까? 아니면 같은 이유로 지난 주 동안 피해야만 했거나 두려움을 느꼈던 다른 활동이 있었습니까? 만약 어느 한 경우라도 그렇다면 지난 주 동안 활동에 대한 공포와 회피를 평가해 주십시오.', 7),
                                                              ('6. 지난주 동안 상기 증상들 모두가 (공황, 제한된 증상 발작, 발작과 관련된 걱정, 발작 때문에 상황이나 활동을 두려워함) 집안일을 하거나 직장에서 업무를 수행하는 데 얼마나 방해를 했습니까? (지난주 동안의 집안일이나 직장에서의 업무가 평소보다 적었다면 만약 일상적인 양이었다면 얼마나 할 수 있었을지를 고려해 응답해 주십시오.)', 7),
                                                              ('7. 지난주 동안 공황발작, 제한된 증상 발작, 발작과 관련된 걱정, 발작으로 인한 상황 및 활동에 대한 두려움이 사회 활동을 얼마나 방해했습니까? (지난 주 동안 사회적 활동을 할 기회가 없었다면 만약 기회가 있었을 경우 어떻게 했을지 생각해서 응답해 주십시오.)', 7);
INSERT INTO psycho_question (question_text, test_type_id) VALUES
                                                              ('1. 그 경험에 관한 악몽을 꾸거나, 생각하고 싶지 않은데도 그 경험이 떠오른 적이 있었다.', 8),
                                                              ('2. 그 경험에 대해 생각하지 않으려고 애쓰거나, 그 경험을 떠오르게 하는 상황을 피하기 위해 특별히 노력하였다.', 8),
                                                              ('3. 늘 주변을 살피고 경계하거나, 쉽게 놀라게 되었다.', 8),
